import React, { useState } from 'react';
import axios from 'axios';

const SubmitRecipe = () => {
  const [formData, setFormData] = useState({
    name: '',
    ingredients: '',
    steps: '',
    calories: '',
    tags: '',
    image: null,
    imageURL: '',
  });
  const [useLink, setUseLink] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formData.name);
    data.append('ingredients', JSON.stringify(formData.ingredients.split(',')));
    data.append('steps', JSON.stringify(formData.steps.split('.')));
    data.append('calories', formData.calories);
    data.append('tags', JSON.stringify(formData.tags.split(',')));
    if (!useLink && formData.image) {
      data.append('image', formData.image);
    } else if (useLink && formData.imageURL) {
      data.append('imageURL', formData.imageURL);
    }

    try {
      const response = await axios.post('http://localhost:1240/submit-recipe', data);
      alert(response.data.message);
    } catch (error) {
      alert('Error submitting recipe');
    }
  };

  return (
    <div className="submit-recipe-container">
      <h1>Submit a Recipe</h1>
      /*{message && <p className="message">{message}</p>}
      <form onSubmit={handleSubmit} className="submit-recipe-form">
        <label>
          Recipe Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Enter recipe name"
            required
          />
        </label>

        <label>
          Ingredients (comma-separated):
          <textarea
            name="ingredients"
            value={formData.ingredients}
            onChange={handleChange}
            placeholder="e.g., pasta, vegetables, cream"
            required
          />
        </label>

        <label>
          Steps (period-separated):
          <textarea
            name="steps"
            value={formData.steps}
            onChange={handleChange}
            placeholder="e.g., Boil pasta. SautÃ© vegetables. Mix and serve."
            required
          />
        </label>

        <label>
          Calories:
          <input
            type="number"
            name="calories"
            value={formData.calories}
            onChange={handleChange}
            placeholder="Enter calorie count"
            required
          />
        </label>

        <label>
          Tags (comma-separated):
          <input
            type="text"
            name="tags"
            value={formData.tags}
            onChange={handleChange}
            placeholder="e.g., Italian, Vegetarian"
            required
          />
        </label>

        <label>
          Upload Image:
          <input type="file" name="image" onChange={handleChange} />
        </label>

        <button type="submit" className="submit-button">
          Submit Recipe
        </button>
      </form>
    </div>
  );
};

export default SubmitRecipe;
