import React, { useState } from 'react';
import axios from 'axios';

const SubmitRecipe = () => {
  const [formData, setFormData] = useState({
    name: '',
    ingredients: '',
    steps: '',
    calories: '',
    tags: '',
    image: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formData.name);
    data.append('ingredients', JSON.stringify(formData.ingredients.split(',')));
    data.append('steps', JSON.stringify(formData.steps.split('.')));
    data.append('calories', formData.calories);
    data.append('tags', JSON.stringify(formData.tags.split(',')));
    if (formData.image) data.append('image', formData.image);

    try {
      const response = await axios.post('http://localhost:1240/submit-recipe', data);
      alert(response.data.message);
    } catch (error) {
      alert('Error submitting recipe');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" placeholder="Recipe Name" onChange={handleChange} required />
      <textarea name="ingredients" placeholder="Ingredients (comma-separated)" onChange={handleChange} required />
      <textarea name="steps" placeholder="Steps (period-separated)" onChange={handleChange} required />
      <input name="calories" type="number" placeholder="Calories" onChange={handleChange} required />
      <input name="tags" placeholder="Tags (comma-separated)" onChange={handleChange} />
      <input type="file" onChange={handleFileChange} />
      <button type="submit">Submit Recipe</button>
    </form>
  );
};

export default SubmitRecipe;
