import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/ViewRecipes.css';

const ViewRecipes = () => {
  const [loading, setLoading] = useState(true);
  const [recipes, setRecipes] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get('http://localhost:1240/view-recipe');
        setRecipes(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch submitted recipes.');
        setLoading(false);
      }
    };
    fetchRecipes();
  }, []);

  const today = new Date();

  if (loading) {
    return <div className="loading">Loading submitted recipes...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="recipe-container">
      <header className="recipe-header">
        <div className="recipe-date">
          {today.toLocaleDateString('en-US', {
            weekday: 'long',
            year: 'numeric',
            month: 'long',
            day: 'numeric',
          })}
        </div>
        <h1 className="recipe-title">Submitted Recipes</h1>
      </header>

      <div className="recipe-grid">
        {recipes.map((recipe) => (
          <div key={recipe._id} className="recipe-card">
            <h2 className="recipe-name">{recipe.name}</h2>

            {recipe.image && (
              <div className="recipe-image">
                <img
                  src={recipe.image_link} 
                  alt={recipe.name}
                  style={{ width: '100%' }}
                />
              </div>
            )}

            <div className="recipe-info">
              <span>ðŸ”¥ Calories: {recipe.calories} kcal</span>
              <span>Tags: {recipe.tags.join(', ')}</span>
            </div>

            <div className="recipe-section">
              <h3 className="section-title">Ingredients</h3>
              <ul className="recipe-list">
                {recipe.ingredients.map((ingredient, index) => (
                  <li key={index} className="recipe-list-item">
                    {ingredient}
                  </li>
                ))}
              </ul>
            </div>

            <div className="recipe-section">
              <h3 className="section-title">Steps</h3>
              <ol className="recipe-steps">
              {recipe.steps
               .filter((step) => step && step.trim() !== "") // Filter out empty or invalid steps
               .map((step, index) => (
              <li key={index} className="recipe-step-item">
               {step}
        </li>
      ))}
  </ol>
</div>

          </div>
        ))}
      </div>
    </div>
  );
};

export default ViewRecipes;
