import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const Search = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [error, setError] = useState(null); // State to handle errors
  const navigate = useNavigate(); 
  // Fetch all recipes on component mount
  useEffect(() => {
    const fetchRecipes = async () => {
        try {
            const response = await axios.get("http://localhost:1240/search");
            setRecipes(response.data);
        } catch (err) {
            setError('Failed to fetch recipes.');
        }
    };
    fetchRecipes();
}, []);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = async (e) => {
    e.preventDefault();

    if (searchQuery.trim() === '') {
      setError('Please enter a search term.');
      return;
    }

    try {
      const response = await axios.get("http://localhost:1240/Search",{ params: { q: searchQuery } });
      setError(null); // Clear any previous errors

      // Redirect to the RecipeDetails page with the recipe ID
      navigate(`../RecipeDetails/${response.data._id}`);
    } catch (err) {
      setError(err.response?.data.message || 'Error searching//// for recipe.///');
    }
  };

  // Filter recipes based on the search query
  const filteredRecipes = recipes.filter(recipe =>
    recipe.recipe_name.toLowerCase().includes(searchQuery.toLowerCase())
);

// Inline styles
const styles = {
  searchResults: {
      marginTop: '20px',
  },
  recipeItem: {
      padding: '10px',
      border: '1px solid #ccc',
      borderRadius: '5px',
      marginBottom: '10px',
      backgroundColor: '#f9f9f9',
      cursor: 'pointer',
      width: '200%', // Make the box longer horizontally
      height: '30px', // Make the box shorter in height
      display: 'flex',
      alignItems: 'center',   
  },
  recipeItemHover: {
      backgroundColor: '#e9e9e9',
  },
  errorText: {
      color: 'red',
  },
  searchForm: {
      marginBottom: '20px',
  },
  searchInput: {
      padding: '10px',
      width: '300px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      marginRight: '10px',
  },
  searchButton: {
      padding: '10px 20px',
      borderRadius: '5px',
      border: '1px solid #007bff',
      backgroundColor: '#007bff',
      color: '#fff',
      cursor: 'pointer',
  },
};

return (
  <div>
      <form onSubmit={handleSearchSubmit} style={styles.searchForm}>
          <input
              type="text"
              placeholder="Search recipes by name"
              value={searchQuery}
              onChange={handleSearchChange}
              style={styles.searchInput}
          />
          <button type="submit" style={styles.searchButton}>Search</button>
      </form>

      {error && <p style={styles.errorText}>{error}</p>}

      <div style={styles.searchResults}>
          {filteredRecipes.map((recipe) => (
              <div
                  key={recipe._id}
                  style={styles.recipeItem}
                  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = styles.recipeItemHover.backgroundColor}
                  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = styles.recipeItem.backgroundColor}
                  onClick={() => navigate(`/RecipeDetails/${recipe._id}`)}
              >
                  <h3>{recipe.recipe_name}</h3>
              </div>
          ))}
      </div>
  </div>
);
};

export default Search;
