import React, { useState } from 'react';
import axios from 'axios';

const SubmitRecipe = () => {
  const [formData, setFormData] = useState({
    name: '',
    ingredients: '',
    steps: '',
    calories: '',
    tags: '',
    image: null,
    imageURL: '',
  });
  const [useLink, setUseLink] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formData.name);
    data.append('ingredients', JSON.stringify(formData.ingredients.split(',')));
    data.append('steps', JSON.stringify(formData.steps.split('.')));
    data.append('calories', formData.calories);
    data.append('tags', JSON.stringify(formData.tags.split(',')));
    if (!useLink && formData.image) {
      data.append('image', formData.image);
    } else if (useLink && formData.imageURL) {
      data.append('imageURL', formData.imageURL);
    }

    try {
      const response = await axios.post('/submit-recipe', data);
      alert(response.data.message);
    } catch (error) {
      alert('Error submitting recipe');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" placeholder="Recipe Name" onChange={handleChange} required />
      <textarea name="ingredients" placeholder="Ingredients (comma-separated)" onChange={handleChange} required />
      <textarea name="steps" placeholder="Steps (period-separated)" onChange={handleChange} required />
      <input name="calories" type="number" placeholder="Calories" onChange={handleChange} required />
      <input name="tags" placeholder="Tags (comma-separated)" onChange={handleChange} />

      <div>
        <label>
          <input type="radio" checked={!useLink} onChange={() => setUseLink(false)} /> Upload Image
        </label>
        <label>
          <input type="radio" checked={useLink} onChange={() => setUseLink(true)} /> Paste Image Link
        </label>
      </div>

      {!useLink && <input type="file" onChange={handleFileChange} />}
      {useLink && <input name="imageURL" placeholder="Paste image URL" onChange={handleChange} />}

      <button type="submit">Submit Recipe</button>
    </form>
  );
};

export default SubmitRecipe;
