import React, { useState, useEffect } from "react";
import axios from "axios";

function TrendingRecipe() {
  const [recipes, setRecipes] = useState([]);

  // Fetch top 3 recipes from the backend
  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("http://localhost:1241/trending");
        setRecipes(response.data);
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };

    fetchRecipes();
  }, []);

  // Handle like and dislike updates
  const handleVote = async (recipeId, voteType) => {
    try {
      const updatedRecipes = recipes.map((recipe) =>
        recipe.id === recipeId
          ? {
              ...recipe,
              like_count: voteType === "like" ? recipe.like_count + 1 : recipe.like_count,
              dislike_count: voteType === "dislike" ? recipe.dislike_count + 1 : recipe.dislike_count,
            }
          : recipe
      );
      setRecipes(updatedRecipes);

      await axios.put(`http://localhost:1241/recipes/${recipeId}`, {
        like_count: voteType === "like" ? 1 : 0,
        dislike_count: voteType === "dislike" ? 1 : 0,
      });
    } catch (error) {
      console.error("Error updating vote:", error);
    }
  };

  return (
    <div className="trending-recipes container mx-auto py-6">
      <h1 className="text-3xl font-semibold text-center mb-6">Trending Recipes</h1>
      <div className="recipe-list">
        {recipes.map((recipe) => (
          <div key={recipe.id} className="recipe-card card mb-6 shadow-lg">
            <div className="card-body p-4">
              <img
                src={recipe.image_link}
                alt={recipe.recipe_name}
                className="w-full h-64 object-cover rounded-lg mb-4"
              />
              <h4 className="text-xl font-semibold text-blue-600 mb-2">
                {recipe.recipe_name}
              </h4>
              <p className="text-sm text-gray-700 mb-4">Cuisine: {recipe.cuisine}</p>
              <div className="actions flex justify-between">
                <button
                  className="btn btn-success"
                  onClick={() => handleVote(recipe.id, "like")}
                >
                  {recipe.like_count} Likes
                </button>
                <button
                  className="btn btn-error"
                  onClick={() => handleVote(recipe.id, "dislike")}
                >
                  {recipe.dislike_count} Dislikes
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TrendingRecipe;
