import React, { useState, useEffect } from "react";
import axios from "axios";

function TrendingRecipe() {
  const [recipes, setRecipes] = useState([]);

  // Fetch recipes from the backend
  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("http://localhost:1240/api/trending-recipes");
        setRecipes(response.data);
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };

    fetchRecipes();
  }, []);

  // Handle like and dislike actions
  const handleVote = async (index, recipeId, voteType) => {
    try {
      // Optimistically update the UI
      const updatedRecipes = [...recipes];
      const recipe = updatedRecipes[index];
  
      if (voteType === "like") {
        recipe.like_count += 1;
      } else {
        recipe.dislike_count += 1;
      }
      setRecipes(updatedRecipes);
  
      // Send vote update to the backend using the `id` field
      const response = await axios.put(`http://localhost:1240/api/recipe/vote/${recipeId}`, {
        like_count: recipe.like_count,
        dislike_count: recipe.dislike_count,
      });
  
      console.log("Backend response:", response.data); // Debugging log
    } catch (error) {
      console.error(`Error updating ${voteType} for recipe ${recipeId}:`, error);
      alert("Failed to update vote. Please try again.");
    }
  };
  

  return (
    <div className="trending-recipes">
      <h1>Trending Recipes</h1>
      <div className="recipe-list d-flex flex-wrap justify-content-center">
        {recipes.map((recipe, index) => (
          <div key={recipe.id} className="recipe-card">
            <img
              src={recipe.image_link}
              alt={recipe.recipe_name}
              style={{ height: "200px", objectFit: "cover" }}
            />
            <h3>{recipe.recipe_name}</h3>
            <p><strong>Cuisine:</strong> {recipe.cuisine}</p>
            <p><strong>Diet:</strong> {recipe.diet_type}</p>
            <div className="recipe-actions d-flex justify-content-center">
              <button
                className="btn btn-success"
                onClick={() => handleVote(index, recipe.id, "like")}
              >
                Like {recipe.like_count}
              </button>
              <button
                className="btn btn-danger"
                onClick={() => handleVote(index, recipe.id, "dislike")}
              >
                Dislike {recipe.dislike_count}
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TrendingRecipe;
