import React, { useState, useEffect } from "react";

function TrendingRecipe() {
  const [recipes, setRecipes] = useState([]);
  const [likes, setLikes] = useState([]);
  const [dislikes, setDislikes] = useState([]);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch("http://localhost:1240/api/trending-recipes");
        if (!response.ok) throw new Error("Failed to fetch recipes");

        const data = await response.json();
        setRecipes(data);
        setLikes(data.map((recipe) => recipe.like_count || 0));
        setDislikes(data.map((recipe) => recipe.dislike_count || 0));
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };

    fetchRecipes();
  }, []);

  const handleLike = async (index, id) => {
    console.log(`Like button clicked for recipe ${id}`);
    try {
      const response = await fetch(`http://localhost:1240/api/recipe/${id}/like`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ action: "like" }),
      });

      if (!response.ok) throw new Error("Failed to update like");

      const updatedRecipe = await response.json();
      const newLikes = [...likes];
      newLikes[index] = updatedRecipe.like_count;
      setLikes(newLikes);
    } catch (error) {
      console.error("Error liking recipe:", error);
    }
  };

  const handleDislike = async (index, id) => {
    console.log(`Dislike button clicked for recipe ${id}`);
    try {
      const response = await fetch(`http://localhost:1240/api/recipe/${id}/like`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ action: "dislike" }),
      });

      if (!response.ok) throw new Error("Failed to update dislike");

      const updatedRecipe = await response.json();
      const newDislikes = [...dislikes];
      newDislikes[index] = updatedRecipe.dislike_count;
      setDislikes(newDislikes);
    } catch (error) {
      console.error("Error disliking recipe:", error);
    }
  };

  return (
    <div className="trending-recipes">
      <h1>Trending Recipes</h1>
      <div className="recipe-list d-flex flex-wrap justify-content-center">
        {recipes.map((recipe, index) => (
          <div key={recipe._id} className="recipe-card">
            <img
              src={recipe.image_link}
              alt={recipe.recipe_name}
              style={{ height: "200px", objectFit: "cover" }}
            />
            <h3>{recipe.recipe_name}</h3>
            <div className="recipe-actions d-flex justify-content-center">
              <button
                className="btn btn-success"
                onClick={() => handleLike(index, recipe._id)}
              >
                Like {likes[index]}
              </button>
              <button
                className="btn btn-danger"
                onClick={() => handleDislike(index, recipe._id)}
              >
                Dislike {dislikes[index]}
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TrendingRecipe;
