import React, { useState, useEffect } from "react";
import axios from "axios";

function AllRecipes() {
  const [recipes, setRecipes] = useState([]);
  const [reporting, setReporting] = useState(null);
  const [reportMessage, setReportMessage] = useState("");
  const [showModal, setShowModal] = useState(false);

  // Fetch all recipes
  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("http://localhost:1240/api/all-recipes");
        setRecipes(response.data);
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };

    fetchRecipes();
  }, []);

  // Handle reporting
  const handleReport = async (recipeId) => {
    try {
      if (!reportMessage.trim()) {
        alert("Please provide a report message.");
        return;
      }

      await axios.post(`http://localhost:1240/api/recipe/report/${recipeId}`, {
        reportMessage,
      });

      alert("Report submitted successfully.");
      setReporting(null);
      setReportMessage("");
      setShowModal(false); // Close modal
    } catch (error) {
      console.error("Error reporting recipe:", error);
      alert("Failed to submit the report. Please try again.");
    }
  };

  const openModal = (recipeId) => {
    setReporting(recipeId);
    setShowModal(true);
  };

  const closeModal = () => {
    setReporting(null);
    setReportMessage("");
    setShowModal(false);
  };

  return (
    <div className="all-recipes">
      <h1 className="title">All Recipes</h1>
      <div className="recipe-list">
        {recipes.map((recipe) => (
          <div key={recipe.id} className="recipe-card">
            <img
              src={recipe.image_link}
              alt={recipe.recipe_name}
              className="recipe-image"
            />
            <div className="recipe-content">
              <h3 className="recipe-name">{recipe.recipe_name}</h3>
              <p className="recipe-info">
                <strong>Cuisine:</strong> {recipe.cuisine}
              </p>
              <p className="recipe-info">
                <strong>Diet:</strong> {recipe.diet_type}
              </p>
              <button
                className="btn btn-report"
                onClick={() => openModal(recipe.id)}
              >
                Report
              </button>
            </div>
          </div>
        ))}
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Report Recipe</h2>
            <textarea
              value={reportMessage}
              onChange={(e) => setReportMessage(e.target.value)}
              placeholder="Explain your issue here..."
              rows="4"
            />
            <div className="modal-actions">
              <button
                className="btn btn-submit-report"
                onClick={() => handleReport(reporting)}
              >
                Submit
              </button>
              <button className="btn btn-cancel" onClick={closeModal}>
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AllRecipes;
