import React, { useState } from 'react';
import axios from 'axios';
import '../css/SubmitRecipe.css';

const SubmitRecipe = () => {
  const [formData, setFormData] = useState({
    name: '',
    ingredients: '',
    steps: '',
    calories: '',
    tags: '',
    imageLink: '',
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:1240/submit-recipe', formData);
      setMessage('Recipe submitted successfully!');
      setFormData({ name: '', ingredients: '', steps: '', calories: '', tags: '', imageLink: '' });
    } catch (error) {
      setMessage('Failed to submit the recipe.');
    }
  };

  return (
    <div className="submit-recipe-container">
      <h1>Submit Your Recipe</h1>
      <form onSubmit={handleSubmit} className="recipe-form">
        <label>
          Recipe Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Ingredients (comma-separated):
          <textarea
            name="ingredients"
            value={formData.ingredients}
            onChange={handleChange}
            required
          ></textarea>
        </label>
        <label>
          Steps (period-separated):
          <textarea
            name="steps"
            value={formData.steps}
            onChange={handleChange}
            required
          ></textarea>
        </label>
        <label>
          Calories:
          <input
            type="number"
            name="calories"
            value={formData.calories}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Tags (comma-separated):
          <input
            type="text"
            name="tags"
            value={formData.tags}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Image Link:
          <input
            type="url"
            name="imageLink"
            value={formData.imageLink}
            onChange={handleChange}
            required
          />
        </label>
        <button type="submit" className="submit-button">Submit Recipe</button>
      </form>
      {message && <div className="message">{message}</div>}
    </div>
  );
};

export default SubmitRecipe;
