import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ViewRecipes = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get('http://localhost:1240/view-recipes');
        setRecipes(response.data);
      } catch (error) {
        console.error('Error fetching recipes:', error);
      }
    };
    fetchRecipes();
  }, []);

  return (
    <div>
      <h1>Submitted Recipes</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px' }}>
        {recipes.map((recipe) => (
          <div key={recipe._id} style={{ border: '1px solid #ccc', padding: '16px', width: '300px' }}>
            <h2>{recipe.name}</h2>
            {recipe.image && <img src={`http://localhost:1240/${recipe.image}`} alt={recipe.name} style={{ width: '100%' }} />}
            <p><strong>Calories:</strong> {recipe.calories} kcal</p>
            <p><strong>Tags:</strong> {recipe.tags.join(', ')}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ViewRecipes;
