// SearchBar.js
import React, { useState } from 'react';
import axios from 'axios';

const Search = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [error, setError] = useState(null);
  const [searchResults, setSearchResults] = useState({});

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = async (e, searchType) => {
    e.preventDefault();

    if (searchQuery.trim() === '') {
      setError('Please enter a search term.');
      return;
    }

    try {
      const response = await axios.get(`http://localhost:1240/${searchType}`, { params: { q: searchQuery } });
      setError(null);
      setSearchResults(response.data);
    } catch (err) {
      setError(err.response?.data.message || 'Error searching for recipe.');
    }
  };

  return (
    <div>
      <h2>Search Recipes</h2>

      <form onSubmit={(e) => handleSearchSubmit(e, 'search-recipe-name')}>
        <input
          type="text"
          placeholder="Search by recipe name"
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <button type="submit">Search</button>
      </form>

      <form onSubmit={(e) => handleSearchSubmit(e, 'search-ingredient')}>
        <input
          type="text"
          placeholder="Search by ingredient"
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <button type="submit">Search</button>
      </form>

      <form onSubmit={(e) => handleSearchSubmit(e, 'search-cuisine')}>
        <input
          type="text"
          placeholder="Search by cuisine"
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <button type="submit">Search</button>
      </form>

      <form onSubmit={(e) => handleSearchSubmit(e, 'search-diet-type')}>
        <input
          type="text"
          placeholder="Search by diet type"
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <button type="submit">Search</button>
      </form>

      <form onSubmit={(e) => handleSearchSubmit(e, 'search-calories')}>
        <input
          type="text"
          placeholder="Search by calories"
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <button type="submit">Search</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {searchResults && (
        <div>
          <h2>Search Results</h2>
          <ul>
            {searchResults.map((recipe, index) => (
              <li key={index}>
                <h3>{recipe.recipe_name}</h3>
                <p>Ingredients: {recipe.ingredient_list.join(', ')}</p>
                <p>Cuisine: {recipe.cuisine}</p>
                <p>Diet Type: {recipe.diet_type}</p>
                <p>Calories: {recipe.calories}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Search;
