import React, { useState, useEffect } from "react";
import axios from "axios";

function Spoon() {
  const [recipes, setRecipes] = useState([]); // List of recipes
  const [searchTerm, setSearchTerm] = useState(""); // Search input
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(""); // Error state

  // Fetch 50 recipes on page load
  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = async (query = "") => {
    setLoading(true);
    setError("");
    try {
      const apiKey = "YOUR_SPOONACULAR_API_KEY"; // Replace with your Spoonacular API key
      const response = await axios.get(
        `https://api.spoonacular.com/recipes/complexSearch?number=50&query=${query}&apiKey=${apiKey}`
      );
      setRecipes(response.data.results); // Set recipes from response
    } catch (err) {
      console.error("Error fetching recipes:", err);
      setError("Failed to fetch recipes. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    fetchRecipes(searchTerm); // Fetch recipes matching the search term
  };

  return (
    <div className="recipe-page">
      <h1>Discover Recipes</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for recipes..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
        <button onClick={handleSearch} className="search-button">
          Search
        </button>
      </div>
      {loading && <p>Loading recipes...</p>}
      {error && <p className="error-message">{error}</p>}
      <div className="recipe-grid">
        {recipes.map((recipe) => (
          <div key={recipe.id} className="recipe-card">
            <img src={recipe.image} alt={recipe.title} className="recipe-image" />
            <h3>{recipe.title}</h3>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Spoon;
