import React, { useState, useEffect } from "react";
import axios from "axios";

function TrendingRecipe() {
  const [recipes, setRecipes] = useState([]);

  // Fetch recipes from the backend using axios
  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("http://localhost:1240/api/trending-recipes");
        setRecipes(response.data);
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };

    fetchRecipes();
  }, []);

  // Handle like and dislike updates
  const handleVote = async (recipeId, voteType) => {
    try {
      // Find the recipe by its _id
      const recipeToUpdate = recipes.find((recipe) => recipe._id === recipeId);

      if (!recipeToUpdate) return; // If no recipe is found, exit early

      // Prepare the updated vote count
      const updatedVotes = {
        like_count: voteType === "like" ? recipeToUpdate.like_count + 1 : recipeToUpdate.like_count,
        dislike_count: voteType === "dislike" ? recipeToUpdate.dislike_count + 1 : recipeToUpdate.dislike_count,
      };

      // Optimistically update the state
      const updatedRecipes = recipes.map((recipe) =>
        recipe._id === recipeId
          ? { ...recipe, ...updatedVotes }
          : recipe
      );
      setRecipes(updatedRecipes);

      // Send the updated votes to the backend
      const response = await axios.put(`http://localhost:1240/api/recipe/${recipeId}`, updatedVotes);

      // Sync with backend response (optional)
      if (response.data) {
        const refreshedRecipes = await axios.get("http://localhost:1240/api/trending-recipes");
        setRecipes(refreshedRecipes.data);
      }
    } catch (error) {
      console.error("Error updating votes:", error);
      alert("Failed to update vote. Please try again.");

      // Rollback UI if request fails
      const rolledBackRecipes = recipes.map((recipe) =>
        recipe._id === recipeId
          ? {
              ...recipe,
              like_count: voteType === "like" ? recipe.like_count - 1 : recipe.like_count,
              dislike_count: voteType === "dislike" ? recipe.dislike_count - 1 : recipe.dislike_count,
            }
          : recipe
      );
      setRecipes(rolledBackRecipes);
    }
  };

  return (
    <div className="trending-recipes container mx-auto py-6">
      <h1 className="text-3xl font-semibold text-center mb-6">
        Trending Recipes
      </h1>
      <div className="recipe-list">
        {recipes.map((recipe) => (
          <div
            key={recipe._id}
            className="recipe-card card mb-6 shadow-lg hover:shadow-xl transition-shadow duration-300"
          >
            <div className="card-body flex items-center p-4">
              <img
                src={recipe.image_link}
                alt={recipe.recipe_name}
                className="w-36 h-36 object-cover rounded-lg mr-4"
              />
              <div className="content ml-4 flex-1">
                <h4 className="text-xl font-semibold text-blue-600 mb-2">
                  {recipe.recipe_name}
                </h4>
                <p className="text-sm text-gray-700 mb-4">
                  {recipe.cuisine} | {recipe.diet_type} | {recipe.calories} cal
                </p>
                <div className="actions flex gap-4">
                  <button
                    className="btn btn-success"
                    onClick={() => handleVote(recipe._id, "like")}
                  >
                    {recipe.like_count} Likes
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleVote(recipe._id, "dislike")}
                  >
                    {recipe.dislike_count} Dislikes
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TrendingRecipe;
