// SearchBar.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

// Get the search bars and buttons
const searchRecipeNameInput = document.getElementById('search-recipe-name');
const searchRecipeNameBtn = document.getElementById('search-recipe-name-btn');

const searchIngredientInput = document.getElementById('search-ingredient');
const searchIngredientBtn = document.getElementById('search-ingredient-btn');

const searchCuisineInput = document.getElementById('search-cuisine');
const searchCuisineBtn = document.getElementById('search-cuisine-btn');

// Add event listeners to the buttons
searchRecipeNameBtn.addEventListener('click', searchRecipeByName);
searchIngredientBtn.addEventListener('click', searchByIngredient);
searchCuisineBtn.addEventListener('click', searchByCuisine);

// Search functions
async function searchRecipeByName() {
  const query = searchRecipeNameInput.value;
  const response = await fetch(`/search-recipe-name?q=${query}`);
  const data = await response.json();
  console.log(data);
}

async function searchByIngredient() {
  const query = searchIngredientInput.value;
  const response = await fetch(`/search-ingredient?q=${query}`);
  const data = await response.json();
  console.log(data);
}

async function searchByCuisine() {
  const query = searchCuisineInput.value;
  const response = await fetch(`/search-cuisine?q=${query}`);
  const data = await response.json();
  console.log(data);
}


export default Search;
